import React, { useState, useEffect } from "react";
import "./App.css";
import { socket } from "./sockets";

function App() {
  const [isConnected, setIsConnected] = useState(socket.connected);
  const [isMatchWaiting, setIsMatchWaiting] = useState(null);
  const [isMatchFound, setIsMatchFound] = useState(null);

  useEffect(() => {
    function onConnect() {
      setIsConnected(true);
    }

    function onDisconnect() {
      setIsConnected(false);
    }

    socket.on("connect", onConnect);
    socket.on("disconnect", onDisconnect);

    return () => {
      socket.off("connect", onConnect);
      socket.off("disconnect", onDisconnect);
    };
  }, []);

  const handleCreateLobby = () => {
    socket.emit("lobby:join", {
      name: "pratik kumar",
      email: "prateektiwari378@gmail.com",
    });
  };

  socket.on("match:waiting", (args) => {
    if (args?.message) {
      setIsMatchWaiting(true);
    }
  });

  socket.on("joinRoom", (data) => {
    socket.emit("joinRoom", data.room);
  });

  socket.on("match:done", (args) => {
    setIsMatchFound(args);
  });

  return (
    <div className="App">
      <h1>ALL SET YOU ARE CONNECTED</h1>
      {isMatchFound ? (
        <>
          <p>Total users connected :- </p>
          <p>User 1 {isMatchFound?.user1}</p>
          <p>User 2 {isMatchFound?.user2}</p>
        </>
      ) : isMatchWaiting ? (
        "Please wait, We are connecting you with other users"
      ) : (
        <>
          <button onClick={handleCreateLobby}>Join lobby</button>
        </>
      )}
    </div>
  );
}

export default App;
