import React, { useState, useEffect } from "react";
import logo from "./logo.svg";
import "./App.css";
import { socket } from "./sockets";

function App() {
  const [isConnected, setIsConnected] = useState(socket.connected);
  const [lobbyCreated, setLobbyCreated] = useState(null);
  useEffect(() => {
    function onConnect() {
      setIsConnected(true);
    }

    function onDisconnect() {
      setIsConnected(false);
    }

    socket.on("connect", onConnect);
    socket.on("disconnect", onDisconnect);

    return () => {
      socket.off("connect", onConnect);
      socket.off("disconnect", onDisconnect);
    };
  }, []);
  const handleCreateLobby = () => {
    console.log("emitting event");
    socket.emit("lobby:create", {
      name: "pratik kumar",
      email: "prateektiwari378@gmail.com",
    });
  };

  socket.on("lobby:added", (args) => {
    console.log("LOBBY CREATED EVENT RECEIVED", args);
    setLobbyCreated(args);
  });

  return (
    <div className="App">
      <h1>ALL SET YOU ARE CONNECTED</h1>
      <button onClick={handleCreateLobby}>
        {!lobbyCreated ? (
          "Make Lobby"
        ) : (
          <>
            {lobbyCreated?.map((curr) => {
              <p>{curr}</p>;
            })}
          </>
        )}
      </button>
    </div>
  );
}

export default App;
