import React, { useState, useEffect } from "react";
import "./App.css";
import { socket } from "./sockets";
import Game from "./Game";

function App() {
  const [isConnected, setIsConnected] = useState(socket.connected);
  const [isMatchWaiting, setIsMatchWaiting] = useState(null);
  const [isMatchFound, setIsMatchFound] = useState(null);
  const [uname, setUname] = useState("");
  const [roomData, setRoomData] = useState(null);

  useEffect(() => {
    function onConnect() {
      setIsConnected(true);
    }

    function onDisconnect() {
      setIsConnected(false);
    }

    socket.on("connect", onConnect);
    socket.on("disconnect", onDisconnect);

    return () => {
      socket.off("connect", onConnect);
      socket.off("disconnect", onDisconnect);
    };
  }, []);

  const handleCreateLobby = () => {
    socket.emit("lobby:join", {
      name: uname,
      token: uname,
    });
  };

  socket.on("match:waiting", (args) => {
    if (args?.message) {
      setIsMatchWaiting(true);
    }
  });

  socket.on("joinRoom", (data) => {
    // data ---> room: room_name,
    //           choice: "HEADS",
    setRoomData(data);
    socket.emit("joinRoom", {
      name: uname,
      token: uname,
      roomName: data.room,
    });
  });

  socket.on("match:done", (args) => {
    setIsMatchFound(args);
  });

  // navigator.permissions
  //   .query({ name: "geolocation" })
  //   .then(function (result) {
  //     if (result.state === "granted") {
  //       // Permission has been granted, you can access geolocation
  //       console.log("Geolocation permission granted");
  //     } else if (result.state === "prompt") {
  //       // The user has not been asked for permission yet, you can request it
  //       navigator.geolocation.getCurrentPosition(
  //         function (position) {
  //           console.log("Geolocation permission granted");
  //           console.log(position);
  //         },
  //         function (error) {
  //           console.log("Geolocation permission denied");
  //         }
  //       );
  //     } else if (result.state === "denied") {
  //       // Permission has been denied, you cannot access geolocation
  //       console.log("Geolocation permission denied");
  //     }
  //   })
  //   .catch(function (error) {
  //     console.log("Error querying geolocation permission", error);
  //   });

  return (
    <div className="App">
      <h1>ALL SET YOU ARE CONNECTED</h1>
      {isMatchFound ? (
        <>
          <p>Total users connected :- </p>
          <p>User 1 {isMatchFound?.user1?.name}</p>
          <p>User 2 {isMatchFound?.user2?.name}</p>
          <Game data={roomData} users={isMatchFound} />
        </>
      ) : isMatchWaiting ? (
        "Please wait, We are connecting you with other users"
      ) : (
        <>
          <input
            placeholder="enter your name"
            onChange={(e) => setUname(e.target.value)}
          ></input>
          <button onClick={handleCreateLobby}>Join lobby</button>
        </>
      )}
    </div>
  );
}

export default App;
