import React, { useEffect, useRef, useState } from "react";
import { socket } from "./sockets";

const Game = ({ data, users }) => {
  const [countdown, setCountdown] = useState(30);
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [isResultDeclared, setIsResultDeclared] = useState(false);
  const [result, setResult] = useState(null)
  const timerRef = useRef(null);

  useEffect(() => {
    if (isTimerRunning) {
      timerRef.current = setTimeout(() => {
        setCountdown((prevCountdown) => {
          if (prevCountdown === 1) {
            handleTimeout();
            return 30;
          } else {
            return prevCountdown - 1;
          }
        });
      }, 1000);
    }

    return () => {
      clearTimeout(timerRef.current);
    };
  }, [countdown, isTimerRunning]);

  const startTimer = () => {
    setCountdown(10);
    setIsTimerRunning(true);
  };

  const handleTimeout = () => {
    socket.emit('result', {room: data?.room})
    setIsTimerRunning(false);
  };

  socket.on('result', async(dto) => {
    setIsResultDeclared(true)
    setResult(dto.result)
  })

  useEffect(() => {
    startTimer();
  }, [])

  return (
    <div>
      <h3>${countdown} seconds to declare the result</h3>
      <h2>USER1: {users?.user1?.name}</h2>
      <h2>USER2: {users?.user2?.name}</h2>
      {
        isResultDeclared && (
          <h1>THE RESULT IS : {result}</h1>
        )
      }
    </div>
  );
};

export default Game;
